import React,  { useEffect, useState } from 'react'
import {Link, useNavigate} from 'react-router-dom'
import Header from '/Users/shreyaprasad/Desktop/Coding /WebDev /planner/client/src/components/common/heading/Header.jsx'
import './clubsignup.css'
import Validation from './ClubSignupValidation'
import axios from 'axios'

const ClubSignup = () => {
    const [values, setValues] = useState({
        club: '',
        dept: '',
        bio: '',
        email: '',
        password: '',
        repassword: ''
    })
    const navigate = useNavigate();
    const [errors, setErrors] = useState({})

    const handleInput = (event) => {
        setValues(prev => ({...prev,[event.target.name]:[event.target.value]}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(Validation(values));
        if (errors.club === "" && errors.dept === "" && errors.bio === "" && errors.email === "" && errors.password === "" && errors.repassword === ""){
            axios.post('http://localhost:3000/signup/club', values)
            .then(res => {
                navigate('club/profile')
            })
            .catch(err => console.log(err))
        }
    }

    return (
        <div>
            <Header/>
            <div className='club-signup-flex-container'>
                <form action="" onSubmit={handleSubmit} className='club-signup-form'>
                    <div className='club-signup-form-left'>
                        <h2>Club Signup</h2>
                        <p>Hey there! Are you new to ClubHub? <br></br>Register your club here</p>
                        <button type='submit' class='club-signup-submit-btn'>SIGNUP</button>
                    </div>

                    <div>
                        <input type='text' placeholder='Club Name' name='club'
                           onChange={handleInput} requried/>
                            <br></br>
                            {errors.club && <span className='input-error'> {errors.club} </span>}
                            <br></br>
                        <input type='text' placeholder='Department' name='dept' 
                            onChange={handleInput} requried/>
                            <br></br>
                            {errors.dept && <span className='input-error'> {errors.dept} </span>}
                            <br></br>
                        <input type='text' placeholder='Describe your club in one sentence' name='bio' 
                            onChange={handleSubmit} requried/>
                            <br></br>
                            {errors.bio && <span className='input-error'> {errors.bio} </span>}
                            <br></br>
                        <input type='email' placeholder='Email ID' name='email' 
                            onChange={handleSubmit} requried/>
                            <br></br>
                            {errors.email && <span className='input-error'> {errors.email} </span>}
                            <br></br>
                        <input type='text' placeholder='Enter password' name='password' 
                            onChange={handleSubmit} requried/>
                            <br></br>
                            {errors.password && <span className='input-error'> {errors.password} </span>}
                            <br></br>
                        <input type='text' placeholder='Re-enter password' name='repassword' 
                            onChange={handleSubmit} requried/>
                            <br></br>
                            {errors.repassword && <span className='input-error'> {errors.repassword} </span>}
                            <br></br><br></br>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default ClubSignup
