import React,  { useEffect, useState } from 'react'
import {Link, useParams} from 'react-router-dom'
import './studentprofile.css';
import StudentHeading from '../../common/heading/studentheading/StudentHeading'
import axios from 'axios';

const StudentProfile = () => {
    const initialProfile = {
        srn: 'PES1UG21CS305',
        email: 'mukeshraj@gmail.com',
        phno: '',
        cgpa: '',
        sem: '',
        section: ''
    };

    const initialClub = {
        clubname: '',
        designation: 'Member',
        doj: '',
    };

    const [club, setClub] = useState(initialClub);
    const [profile, setProfile] = useState(initialProfile);
    const [myBool, setmyBool] = useState(true);
    const [myBool2, setmyBool2] = useState(true);
    const [myBool3, setmyBool3] = useState(true);
    const [errors, setErrors] = useState({})

    function toggleBool2() {
        setmyBool2(!myBool2)
    }

    function toggleBool3() {
        setmyBool3(!myBool3)
    }

    function toggleBool() {
        setmyBool(!myBool)
    }

    const handleInputProfile = (e) => {
        setProfile(prev => ({...prev, [e.target.name]:[e.target.value]}))
    }

    const handleInputClub = (e) => {
        setClub(prev => ({...prev, [e.target.name]:[e.target.value]}))
    }

    const handleFormSubmit = (e) => {
        e.preventDefault();
        setErrors(Validation(profile))
        const updatedProfile = {
            srn: e.target.elements.srn.value,
            email: e.target.elements.email.value,
            phno: e.target.elements.phno.value,
            cgpa: e.target.elements.cgpa.value,
            sem: e.target.elements.sem.value,
            section: e.target.elements.section.value
        };

        setProfile(updatedProfile);
        axios.post('http://localhost:8081/student/profile', profile)
        .catch(err => console.log(err))
        toggleBool();
    };

    const handleClubSubmit = (e) => {
        e.preventDefault();

        const updatedClub = {
            clubname: e.target.elements.clubname.value,
            designation: e.target.elements.designation.value,
            doj: e.target.elements.doj.value,
        };

        setClub(updatedClub);
        toggleBool3();
    }


    function HeaderCard(props) {
        return (
            <div>
                <div class='profile-banner'>
                    <div class='profile-image'></div>
                    <div class='profile-heading'>
                        <p class='profile-title'>Student name</p>
                        <p class='profile-student-srn'>SRN</p>
                        <div onClick={props.toggleBool} class='profile-edit-btn'><span>Edit profile</span></div>
                    </div>
                </div>
                <div class='student-about'>
                    <p class='profile-title'>About</p>
                    <div class='profile-section-rect1'>
                        <div className='profile-about-content'>
                            <p>SRN: {profile.srn}</p>
                            <p>Mail ID: {profile.email}</p>
                            <p>Phone number: {profile.phno}</p>
                            <p>CGPA: {profile.cgpa}</p>
                            <p>Semester: {profile.sem}</p>
                            <p>Section: {profile.section}</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    function EditProfile({ profile, handleFormSubmit }) {
        return (
            <div>
                <div class='profile-banner'>
                    <div class='profile-image'></div>
                    <div class='profile-heading'>
                        <p class='profile-title'>Student name</p>
                        <p class='profile-student-srn'>SRN</p>
                    </div>
                </div>
                <div class='student-about'>
                    <p class='profile-title'>About</p>
                    <div class='profile-section-rect1'>
                        <div className='profile-about-content'>
                            <form onSubmit={handleFormSubmit}>
                                <div className='about-form'>
                                <p>SRN: <input type='text' value={profile.srn} name='srn' /></p>
                                    <p>Mail ID: <input type='text' value={profile.email} name='email' /></p>
                                    <p>Phone number: <input onChange={handleInputProfile} type='text' defaultValue={profile.phno} name='phno' /></p>
                                    <p>CGPA: <input onChange={handleInputProfile} type='text' defaultValue={profile.cgpa} name='cgpa' /></p>
                                    <p>Semester: <input onChange={handleInputProfile} type='text' defaultValue={profile.sem} name='sem' /></p>
                                    <p>Section: <input onChange={handleInputProfile} type='text' defaultValue={profile.section} name='section' /></p>
                                    <button type="submit">Submit</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    function Club_Member(props) {
        return (
            <div>
                <button onClick={props.toggleBool2} type='submit'>Add Club</button>
            </div>
        )
    }

    function Club_Member_Edit({ club, handleClubSubmit, toggleBool3 }) {
        return (
            <div>
                <div className='profile-section-rect2'>
                    <form onSubmit={handleClubSubmit}>
                        <label htmlFor='club-member'>Club name:</label>
                        <input type='text' name='clubname' defaultValue={club.clubname}></input>
                        <br></br><br></br>
                        <label htmlFor='designation'>Designation:</label>
                        <input type='text' name='designation' defaultValue={club.designation}></input>
                        <br></br><br></br>
                        <label htmlFor='joining-date'>Date of joining:</label>
                        <input type='date' name='doj' defaultValue={club.doj}></input>
                        <br></br>
                        <button type='submit'>Submit</button>
                    </form>
                </div>
            </div>
        );
    }


    function Club_Member_Edited(props) {
        return (
            <div>
                <div class='profile-section-rect2'>
                    <p>Club Name: {club.clubname}</p>
                    <p>Designation: {club.designation}</p>
                    <p>Date of Joining: {club.doj}</p>
                </div>
            </div>
        )
    }

    return (
        <>
            <StudentHeading />
            <div class='student-profile-details'>
                {myBool ? <HeaderCard toggleBool={toggleBool} /> : <EditProfile profile={profile} handleFormSubmit={handleFormSubmit} />}
                <div class='student-about'>
                    <p class='profile-title'>Club Membership</p>
                    {myBool2 ? (
                        <Club_Member toggleBool2={toggleBool2} />
                    ) : myBool3 ? (
                        <Club_Member_Edit club={club} handleClubSubmit={handleClubSubmit} toggleBool3={toggleBool3} />
                    ) : (
                        <Club_Member_Edited />
                    )}

                </div>
            </div>
        </>
    )
}

export default StudentProfile
