import React,  { useEffect, useState } from 'react'
import {Link, useParams} from 'react-router-dom'
import './clubprofile.css';
import ClubHeading from '/Applications/XAMPP/xamppfiles/htdocs/planner/client/src/components/common/heading/clubheading/ClubHeading.jsx'
import FooterFeed from '/Applications/XAMPP/xamppfiles/htdocs/planner/client/src/components/common/footer/FooterFeed.jsx'
import axios from 'axios';

const ClubProfile = () => {
    const { email } = useParams();

    const [clubData, setClubData] = useState([]);
  const [formData, setFormData] = useState({
    head: '',
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const clubDetails = await axios.get('http://localhost:8081/'+club);
      setClubData(clubDetails.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsEditing(false);

    formData.email = email;

    axios.post('http://localhost:8081/clubmembers/'+email, formData)
      .then((response) => {
        console.log('Club details updated successfully');
        // Handle any further actions you need after successful update
      })
      .catch((error) => {
        console.error('Error updating club details:', error);
        // Handle error scenarios
      });
  };

  const handleEdit = () => {
    setIsEditing(true);
  };


    /*

    const [value, setValue] = useState({
        email: 'sanskriti@gmail.com',
        web: 'sanskriti.com',
        head: 'Ganga Bai',
    });

    const updateData = e => {
        setValue(prev => ({
            ...value,
            [e.target.name]: e.target.value
        }))
    }

    const submit = e => {
        e.preventDefault()
        return(
            <HeaderCard/>
        )
    }

    function HeaderCard(props){
        return(
            <div>
            <div class='profile-banner'>
                    <div class='profile-image'></div>
                    <div class='profile-heading'>
                        <p class='profile-title-1'>Club Name</p>
                        <p class='profile-club-bio'>Bio</p>
                        <button class='profile-edit-btn' onClick={props.toggleBool}>Edit profile</button>
                    </div>
                </div>
            <div class='student-about'>
                            <p class='profile-title'>About</p>
                            <div class='profile-section1-rect'>
                                <div className='profile-about-content'>
                                    <p>Mail ID: {value.email}</p>
                                    <p>Club Head(s): {value.head}</p>
                                    <p>Website: {value.web}</p>
                                </div>
                            </div>
                        </div>
            </div>
        )
    }

    function EditProfile(props){
        return(
            <div>
            <div class='profile-banner'>
                    <div class='profile-image'></div>
                    <div class='profile-heading'>
                        <p class='profile-title-1'>Club Name</p>
                        <p class='profile-club-bio'>Bio</p>
                    </div>
                </div>
            <div class='student-about'>
                            <p class='profile-title'>About</p>
                            <div class='profile-section1-rect'>
                                <div className='profile-about-content'>
                                    <form>
                                        <div className='about-form'>
                                        <p>Mail ID: <input type='text' onChange={updateData} defaultValue={value.email} name='email'/></p>
                                        <p>Club Head: <input type='text' defaultValue={value.head} name='head'/></p>
                                        <p>Website: <input type='text' defaultValue={value.web} name='web'/></p>
                                        <button type="submit">Submit</button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
            </div>
        )
    }

    const [open1, setOpen1] = React.useState(false);
    const [open2, setOpen2] = React.useState(false);
    const [open3, setOpen3] = React.useState(false);
    const [style, setStyle] = useState("profile-section3-rect");
    const [myBool, setmyBool] = useState(true);


    function toggleBool(){
        setmyBool(!myBool)
    }



    const handleOpenOne = () => {
        setOpen1(!open1);
    };

    const handleOpenTwo = () => {
        setOpen2(!open2);
    };

    const handleOpenThree = () => {
        setOpen3(!open3);
    };

    const changeStyle = () => {
        if(style!="profile-section3-rect") setStyle("profile-section3-rect");
        else setStyle("profile-section3-rect-drop");
    };
    */

    return(
        <>
            <ClubHeading/>
            <div className='profile-banner'>
                <div className='profile-image'></div>
                {studentData.map((student, index) => (
                <div className='profile-heading' key={index}>
                    <p className='profile-title-1'>{student.fname} {student.lname}</p>
                    <p className='profile-student-srn'>{student.srn}</p>
                </div>
                ))}
            </div>
            <div className='student-profile-details'>
                <div>
                <p className='profile-title'>About</p>
                <div className='profile-section-rect'>
                    {studentData.map((student, index) => (
                    <div className='profile-about-content' key={index}>
                        <p>Mail ID: {student.email}</p>
                        <p>Phone number: {student.phno}</p>
                        <p>CGPA: {student.cgpa}</p>
                        <p>Semester: {student.semester}</p>
                        <p>Section: {student.section}</p>
                    </div>
                    ))}
                </div>
                </div>
                <div className='student-about'>
                <p className='profile-title'>Club Membership</p>
                <div className='profile-section-rect2'>
                    {!isEditing ? (

                    <div>
                        <p>
                        Club Name: {formData.club}
                        </p>
                        <p>
                        Designation: {formData.designation}
                        </p>
                        <button onClick={handleEdit}>Edit</button>
                    </div>
                    ) : (
                    <form onSubmit={handleSubmit}>
                        <label>
                        Club Name:
                        <input
                            type="text"
                            name="club"
                            value={formData.club}
                            onChange={handleInputChange}
                            required
                        />
                        </label>
                        <br />
                        <label>
                        Designation:
                        <input
                            type="text"
                            name="designation"
                            value={formData.designation}
                            onChange={handleInputChange}
                            required
                        />
                        </label>
                        <br />
                        <button type="submit">Submit</button>
                    </form>
                    )}
                </div>
                </div>
            </div>

            <Link to='/venue'><button class='event-button'>Book Event Venue</button></Link>

            <FooterFeed/>
        </>
    )
}



export default ClubProfile
