import React, { useState } from 'react';
import './venue_carousel.css'

const Carousel = () => {
    const [num, setNum] = useState(1);

    const incDate = () => {
        if (num1<31)
            setNum(num1 + 1);
    }

    const decDate = () => {
        if (num1> 1)
            setNum(num1 - 1);
    }

    const incMonth = () => {
        if (num2<31)
            setNum(num2 + 1);
    }

    const decMonth = () => {
        if (num2> 1)
            setNum(num2 - 1);
    }

    const incYear = () => {
        if (num3<31)
            setNum(num3 + 1);
    }

    const decYear = () => {
        if (num3> 1)
            setNum(num3 - 1);
    }

    return(
        <>
            <div className='btn_div'>
                <button onClick={decDate}>-</button>
                <p>{num1}</p>
                <button onClick={incDate}>+</button>
            </div>
            <div className='btn_div'>
                <button onClick={decMonth}>-</button>
                <p>{num2}</p>
                <button onClick={incMonth}>+</button>
            </div>
            <div className='btn_div'>
                <button onClick={decYear}>-</button>
                <p>{num3}</p>
                <button onClick={incYear}>+</button>
            </div>
        </>
    )
}

export default Carousel;
