import React, { useState } from 'react';
import './venue_carousel.css'

const Carousel = () => {
    const [num1, setNum1] = useState(1);
    const [num2, setNum2] = useState(1);
    const [num3, setNum3] = useState(2023);

    const incDate = () => {
        if (num1<31)
            setNum1(num1 + 1);
    }

    const decDate = () => {
        if (num1> 1)
            setNum1(num1 - 1);
    }

    const incMonth = () => {
        if (num2<12)
            setNum2(num2 + 1);
    }

    const decMonth = () => {
        if (num2> 1)
            setNum2(num2 - 1);
    }

    const incYear = () => {
        if (num3<2040)
            setNum3(num3 + 1);
    }

    const decYear = () => {
        if (num3> 2023)
            setNum3(num3 - 1);
    }

    return(
        <>
            <div className='btn-container-date'>
                <div className='btn-container-col'>
                    <label>Date</label>
                    <div className='btn-div'>
                        <p><button onClick={decDate}>-</button>
                        <span>{num1}</span>
                        <button onClick={incDate}>+</button></p>
                    </div>
                </div>
                <div className='btn-container-col'>
                    <label>Month</label>
                    <div className='btn-div'>
                        <p><button onClick={decMonth}>-</button>
                        <span>{num2}</span>
                        <button onClick={incMonth}>+</button></p>
                    </div>
                </div>
                <div className='btn-container-col'>
                    <label>Year</label>
                    <div className='btn-div'>
                        <p><button onClick={decYear}>-</button>
                        <span>{num3}</span>
                        <button onClick={incYear}>+</button></p>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Carousel;
