import React,  { useEffect, useState } from 'react'
import {Link} from 'react-router-dom'
import Header from '/Users/shreyaprasad/Desktop/Coding /WebDev /planner/client/src/components/common/heading/Header.jsx'
import './adminsignup.css'
import Validation from './SignupValidation'

const ClubSignup = () => {
    const [values,setValues] = useState({
        institute: '',
        email: '',
        password: '',
        repassword: ''
    })

    const [errors, setErrors] = useState({})

    const handleInput = (event) => {
        setValues(prev => ({...prev,[event.target.name]: [event.target.value]}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(Validation(values));
    }

    return (
        <div>
            <Header/>
            <div className='admin-signup-flex-container'>
                <form action="" onSubmit={handleSubmit} className='admin-signup-form'>
                    <div className='admin-signup-form-left'>
                        <h2>Admin Signup</h2>
                        <p>Register here to keep track of <br></br>student club activities</p>
                        <button type='submit' class='admin-signup-submit-btn'>SIGNUP</button>
                    </div>


                    <div className='admin-signup-form-right'>
                        <input type='text' placeholder='Name of institute' requried/><br></br><br></br>
                        <input type='email' placeholder='Email ID' requried/><br></br><br></br>
                        <input type='text' placeholder='Enter password' requried/><br></br><br></br>
                        <input type='text' placeholder='Re-enter password' requried/><br></br><br></br><br></br>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default ClubSignup
