import React, { useState } from 'react';
import './venue_carousel.css'

const Carousel = () => {
    const [num1, setNum1] = useState(1);
    const [num2, setNum2] = useState(1);
    const [num3, setNum3] = useState(2023);

    const incDate = () => {
        if (num1<31)
            setNum1(num1 + 1);
    }

    const decDate = () => {
        if (num1> 1)
            setNum1(num1 - 1);
    }

    const incMonth = () => {
        if (num2<12)
            setNum2(num2 + 1);
    }

    const decMonth = () => {
        if (num2> 1)
            setNum2(num2 - 1);
    }

    const incYear = () => {
        if (num3<2040)
            setNum3(num3 + 1);
    }

    const decYear = () => {
        if (num3> 2023)
            setNum3(num3 - 1);
    }

    return(
        <>
            <div className='btn-container'>
                <div className='btn-div'>
                    <button onClick={decDate}>-</button>
                    <p>{num1}</p>
                    <button onClick={incDate}>+</button>
                </div>
                <div className='btn-div'>
                    <button onClick={decMonth}>-</button>
                    <p>{num2}</p>
                    <button onClick={incMonth}>+</button>
                </div>
                <div className='btn-div'>
                    <p><button onClick={decYear}>-</button></p>
                    <p>{num3}</p>
                    <button onClick={incYear}>+</button>
                </div>
            </div>
        </>
    )
}

export default Carousel;
