import React, { useState } from 'react';
import './venue_carousel.css'

const Carousel = () => {
    const [num, setNum] = useState(1);

    const incNum = () => {
        if (num<31)
            setNum(num + 1);
    }

    const decNum = () => {
        if (num> 0)
            setNum(num - 1);
    }

    return(
        <>
            <div className='btn_div'>
                <button onClick={incNum}>+</button>
                <h2>{num}</h2>
                <button onClick={decNum}>-</button>
            </div>
        </>
    )
}

export default Carousel;
