function Validation(values) {
    let error = {}

    if (values.userid === ""){
        error.userid = "*Field should not be empty"
    }

    else {
        error.userid =""
    }

    if (values.password ===""){
        error.password = "*Field should not be empty"
    }

    else {
        error.password =""
    }

    const mysql = require("mysql");
    const sql = "SELECT 'student' AS srn, password FROM student WHERE srn = :values.userid AND password = :values.password UNION SELECT 'club' AS club, password FROM club WHERE club = :values.userid AND password = :values.password UNION SELECT 'admin' AS institute, password FROM admin WHERE institute = :values.userid AND password = :values.password"
    if (sql){
        error = "Wrong email or password"
    }

    return error;
}

export default Validation;
