import React,  { useEffect, useState } from 'react'
import {Link,useParams} from 'react-router-dom'
import './clubprofile.css';
import ClubHeading from '/Applications/XAMPP/xamppfiles/htdocs/planner/client/src/components/common/heading/clubheading/ClubHeading.jsx'
import FooterFeed from '/Applications/XAMPP/xamppfiles/htdocs/planner/client/src/components/common/footer/FooterFeed.jsx'
import axios from 'axios';


const ClubProfile = () => {
    const { email } = useParams();

  const [clubData, setClubData] = useState([]);

  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const clubDetails = await axios.get('http://localhost:8081/club/' + email);
      setClubData(clubDetails.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsEditing(false);
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  return (
    <>
      <ClubHeading />
      <div className='profile-banner'>
        <div className='profile-image'></div>
        {clubData.map((club, index) => (
          <div className='profile-heading' key={index}>
            <p className='profile-title-1'>{club.name}</p>
            <p className='profile-student-srn'>{club.domain}</p>
            <div className='profile-edit-btn'><span>Edit profile</span></div>
          </div>
        ))}
      </div>
      <div className='student-profile-details'>
        <div className='student-about'>
          <p className='profile-title'>About</p>
          <div className='profile-section-rect'>
            {clubData.map((club, index) => (
              <div className='profile-about-content' key={index}>
                <p>Mail ID: {club.email}</p>
              </div>
            ))}
          </div>
        </div>
        <div className='student-about'>
          <p className='profile-title'>Club Membership</p>
          <div className='profile-section-rect'>
            {!isEditing ? (

              <div>
                <p>
                  Club Name: {formData.clubName}
                </p>
                <p>
                  Designation: {formData.designation}
                </p>
                <button onClick={handleEdit}>Edit</button>
              </div>
            ) : (
              <form onSubmit={handleSubmit}>
                <label>
                  Club Name:
                  <input
                    type="text"
                    name="clubName"
                    value={formData.clubName}
                    onChange={handleInputChange}
                    required
                  />
                </label>
                <br />
                <label>
                  Designation:
                  <input
                    type="text"
                    name="designation"
                    value={formData.designation}
                    onChange={handleInputChange}
                    required
                  />
                </label>
                <br />
                <button type="submit">Submit</button>
              </form>
            )}
          </div>
        </div>
      </div>
      <FooterFeed/>
    </>
  );
};


export default ClubProfile
