const StudentProfile = () => {
    const { srn } = useParams();

    const [studentData, setStudentData] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const studentDetails = await axios.get('http://localhost:8081/student/'+srn);
            setStudentData(studentDetails.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };


    const [formData, setFormData] = useState({
        clubName: '',
        designation: ''
      });

      const [isEditing, setIsEditing] = useState(true);

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        setIsEditing(false);
      };

      const handleEdit = () => {
        setIsEditing(true);
      };

    return(
        <>
            <StudentHeading/>
            <div class='profile-banner'>
                <div class='profile-image'></div>
                {studentData.map((srn, index) => (
                <div class='profile-heading'>
                    <p class='profile-title-1'>{srn.fname} {srn.lname}</p>
                    <p class='profile-student-srn'>{srn.srn}</p>
                    <div class='profile-edit-btn'><span>Edit profile</span></div>
                </div>
                ))}
            </div>
            <div class='student-profile-details'>
                    <div class='student-about'>
                        <p class='profile-title'>About</p>
                        <div class='profile-section-rect'>
                        {studentData.map((srn, index) => (
                            <div className='profile-about-content'>
                                <p>Mail ID: {srn.email}</p>
                                <p>Phone number: {srn.phno}</p>
                                <p>CGPA: {srn.cgpa}</p>
                                <p>Semester: {srn.semester}</p>
                                <p>Section: {srn.section}</p>
                            </div>
                        ))}
                        </div>
                    </div>
                    <div class='student-about'>
                        <p class='profile-title'>Club Membership</p>
                        <div class='profile-section-rect'>
                            <form class>
                                <label for='club-member'>Club name:</label>
                                <input type='text'></input><br></br><br></br>
                                <label for='designation'>Designation:</label>
                                <input type='text' defaultValue={'Member'}></input><br></br>
                            </form>
                        </div>
                    </div>


    <div>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          <label>
            Club Name:
            <input
              type="text"
              name="clubName"
              value={formData.clubName}
              onChange={handleInputChange}
            />
          </label>
          <br />
          <label>
            Designation:
            <input
              type="text"
              name="designation"
              value={formData.designation}
              onChange={handleInputChange}
            />
          </label>
          <br />
          <button type="submit">Submit</button>
        </form>
      ) : (
        <div>
          <p>
            <strong>Club Name:</strong> {formData.clubName}
          </p>
          <p>
            <strong>Designation:</strong> {formData.designation}
          </p>
          <button onClick={handleEdit}>Edit</button>
        </div>
      )}
    </div>

            </div>
        </>
    )
}

export default StudentProfile
