import React,  { useEffect, useState } from 'react'
import {Link} from 'react-router-dom'
import Header from '/Users/shreyaprasad/Desktop/Coding /WebDev /planner/client/src/components/common/heading/Header.jsx'
import './loginforgot.css'
import Validation from './LoginForgotValidation';

const LoginForgot = () => {
    const [values,setValues] = useState({
        userid: '',
        password: '',
        repassword: ''
    })

    const [errors, setErrors] = useState({})

    const handleInput = (event) => {
        setValues(prev => ({...prev,[event.target.name]: [event.target.value]}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(Validation(values));
    }

    return (
    <div>
        <Header/>
        <div className='login-flex-container'>  
            <h2>Login</h2>
            <p>Enter to retrieve login access</p>
            <form action="" onSubmit={handleSubmit}>
                <div className='login-form-inputs'>
                    <input type='text' placeholder='User ID' name='userid'
                    onChange={handleInput} requried/>
                    <br></br>
                    {errors.userid && <span className='input-error'> {errors.userid} </span>}
                    <br></br>
                    <input type='text' placeholder='Password' name='password'
                    onChange={handleInput} requried/>
                    <br></br>
                    {errors.password && <span className='input-error'> {errors.password} </span>}
                    <br></br>
                    <input type='text' placeholder='Re-enter password' name='repassword'
                    onChange={handleInput} requried/>
                    <br></br>
                    {errors.repassword && <span className='input-error'> {errors.repassword} </span>}
                    <br></br><br></br>
                    <input type='checkbox'/>
                    <label>Remember me</label><br></br>
                </div>
                <button type='submit' class='login-submit-btn'>LOGIN</button>
            </form>
        </div>
    </div>
  )
}

export default LoginForgot
