import React, { useEffect, useState } from 'react';
import axios from 'axios';
import StudentHeading from './common/heading/studentheading/StudentHeading';

const Dummy = () => {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState({ club: '' });

  useEffect(() => {
    fetchData();
  }, [filter]); // Fetch data whenever the filter changes

  const fetchData = () => {
    axios.get('http://localhost:8081/eventarchive/dummy/'+filter.club)
      .then(response => {
        console.log("Value:", response.data);
        setData(response.data || []); // Use an empty array if response.data is null or undefined
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setData([]); // Set data to an empty array in case of an error
      });
  };

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilter(prevFilter => ({ ...prevFilter, [name]: value }));
  };

  return (
    <>
    <StudentHeading/>
      <div>
        <h2>Event Archives</h2>
        <div>

          <label>Club Name:</label>
          <input type="text" name="club" value={filter.club} onChange={handleFilterChange} />
          <button onClick={fetchData}>Filter</button>
        </div>
        <table border="1">
          <thead>
            <tr>
              <th>Event Name</th>
              <th>Club</th>
              <th>Start Date</th>
              <th>Start Time</th>
              <th>End Date</th>
              <th>End Time</th>
              <th>Venue</th>
              <th>No. of registrations</th>
            </tr>
          </thead>
          <tbody>
            {data.map((row) => (
              <tr key={row.id}>
                <td>{row.event_name}</td>
                <td>{row.club}</td>
                <td>{row.start_date}</td>
                <td>{row.start_time}</td>
                <td>{row.end_date}</td>
                <td>{row.end_time}</td>
                <td>{row.venue}</td>
                <td>{row.No_of_Participants}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Dummy;
