import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './studentprofile.css';
import StudentHeading from '../../common/heading/studentheading/StudentHeading';
import axios from 'axios';

const StudentProfile = () => {
  const { srn } = useParams();

  const [studentData, setStudentData] = useState([]);
  const [aboutData, setAboutData] = useState({
    mailId: '',
    phoneNumber: '',
    cgpa: '',
    semester: '',
    section: '',
  });
  const [clubData, setClubData] = useState({
    clubName: '',
    designation: '',
  });
  const [isEditingAbout, setIsEditingAbout] = useState(false);
  const [isEditingClub, setIsEditingClub] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const studentDetails = await axios.get('http://localhost:8081/student/' + srn);
      setStudentData(studentDetails.data);
      setAboutData({
        mailId: studentDetails.data[0].email,
        phoneNumber: studentDetails.data[0].phno,
        cgpa: studentDetails.data[0].cgpa,
        semester: studentDetails.data[0].semester,
        section: studentDetails.data[0].section,
      });
      setClubData({
        clubName: studentDetails.data[0].clubName,
        designation: studentDetails.data[0].designation,
      });
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleInputChangeAbout = (e) => {
    const { name, value } = e.target;
    setAboutData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleInputChange = (e, section) => {
    const { name, value } = e.target;
    if (section === 'about') {
      setAboutData((prevData) => ({ ...prevData, [name]: value }));
    } else if (section === 'club') {
      setClubData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  const handleSubmit = (e, section) => {
    e.preventDefault();
    // Add logic here to update the data using axios or any other method
    if (section === 'about') {
      setIsEditingAbout(false);
    } else if (section === 'club') {
      setIsEditingClub(false);
    }
  };

  const handleEdit = (section) => {
    if (section === 'about') {
      setIsEditingAbout(!isEditingAbout);
    } else if (section === 'club') {
      setIsEditingClub(!isEditingClub);
    }
  };

  return (
    <>
      <StudentHeading />
      <div className='profile-banner'>
        <div className='profile-image'></div>
        {studentData.map((student, index) => (
          <div className='profile-heading' key={index}>
            <p className='profile-title-1'>{student.fname} {student.lname}</p>
            <p className='profile-student-srn'>{student.srn}</p>
          </div>
        ))}
      </div>
      <div className='student-profile-details'>
        <div className='student-about'>
          <p className='profile-title'>About</p>
          <div className='profile-section-rect'>
            {studentData.map((student, index) => (
              <div className='profile-about-content' key={index}>
                {!isEditingAbout ? (
                  <>
                    <p>Mail ID: {student.email}</p>
                    <p>Phone number: {student.phno}</p>
                    <p>CGPA: {student.cgpa}</p>
                    <p>Semester: {student.semester}</p>
                    <p>Section: {student.section}</p>
                    <button onClick={() => handleEdit('about')}>Edit</button>
                  </>
                ) : (
                  <form onSubmit={(e) => handleSubmit(e, 'about')}>
                    <label>
                      Mail ID:
                      <input
                        type="email"
                        name="mailId"
                        value={aboutData.mailId}
                        onChange={handleInputChangeAbout}
                        required
                      />
                    </label>
                    <br />
                    <label>
                      Phone number:
                      <input
                        type="text"
                        name="phoneNumber"
                        value={aboutData.phoneNumber}
                        onChange={handleInputChangeAbout}
                        required
                      />
                    </label>
                    <br />
                    <label>
                      CGPA:
                      <input
                        type="text"
                        name="cgpa"
                        value={aboutData.cgpa}
                        onChange={handleInputChangeAbout}
                        required
                      />
                    </label>
                    <br />
                    <label>
                      Semester:
                      <input
                        type="text"
                        name="semester"
                        value={aboutData.semester}
                        onChange={handleInputChangeAbout}
                        required
                      />
                    </label>
                    <br />
                    <label>
                      Section:
                      <input
                        type="text"
                        name="section"
                        value={aboutData.section}
                        onChange={handleInputChangeAbout}
                        required
                      />
                    </label>
                    <br />
                    <button type="submit">Submit</button>
                  </form>
                )}
              </div>
            ))}
          </div>
        </div>
        <div className='student-about'>
          <p className='profile-title'>Club Membership</p>
          <div className='profile-section-rect'>
            {!isEditingClub ? (
              <div>
                <p>
                  Club Name: {clubData.clubName}
                </p>
                <p>
                  Designation: {clubData.designation}
                </p>
                <button onClick={() => handleEdit('club')}>Edit</button>
              </div>
            ) : (
              <form onSubmit={(e) => handleSubmit(e, 'club')}>
                <label>
                  Club Name:
                  <input
                    type="text"
                    name="clubName"
                    value={clubData.clubName}
                    onChange={(e) => handleInputChange(e, 'club')}
                    required
                  />
                </label>
                <br />
                <label>
                  Designation:
                  <input
                    type="text"
                    name="designation"
                    value={clubData.designation}
                    onChange={(e) => handleInputChange(e, 'club')}
                    required
                  />
                </label>
                <br />
                <button type="submit">Submit</button>
              </form>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default StudentProfile;


