function Validation(values) {
    let error = {}

    const email_pattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/

    if (values.fname === ""){
        error.fname = "*Field should not be empty"
    }

    else {
        error.fname =""
    }

    if (values.lname === ""){
        error.lname = "*Field should not be empty"
    }

    else {
        error.lname =""
    }

    if (values.srn === ""){
        error.srn = "*Field should not be empty"
    }

    else {
        error.srn =""
    }

    if (values.email === ""){
        error.email = "*Field should not be empty"
    }

    else if (!email_pattern.test(values.email)){
        error.email = "*Email didn't match"
    }

    else {
        error.email =""
    }

    if (values.password === ""){
        error.password = "*Field should not be empty"
    }

    else {
        error.password =""
    }

    if (values.repassword === ""){
        error.repassword = "*Field should not be empty"
    }

    else {
        error.repassword =""
    }

    return error;
}

export default Validation;
